<?phpdefined( '_JEXEC' ) or die( 'Restricted access' );jimport( 'joomla.application.component.view');class SharecheckerViewResults extends JView{function display($tpl = null)    {    $model = &$this->getModel();		JToolBarHelper::preferences( 'com_sharechecker', 550);		$this->assignRef( 'LoadResults', $model->LoadResults());		$this->assignRef( 'results', $model->results);		$this->assignRef( 'type', $model->type);		parent::display($tpl);    }} ?>