<?phpdefined( '_JEXEC' ) or die( 'Restricted access' );jimport( 'joomla.application.component.view');class SharecheckerViewSharechecker extends JView{function display($tpl = null)    {    $model = &$this->getModel();		JToolBarHelper::preferences( 'com_sharechecker', 550);		$this->assignRef( 'UpdateAndCheckLinks', $model->UpdateAndCheckLinks());		parent::display($tpl);    }} ?>